CREATE TABLE `SequelizeMeta` (`name` VARCHAR(255) NOT NULL UNIQUE PRIMARY KEY);
CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE `SequelizeData` (`name` VARCHAR(255) NOT NULL UNIQUE PRIMARY KEY);
CREATE TABLE `Users` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `firstName` VARCHAR(255) NOT NULL, `lastName` VARCHAR(255) NOT NULL, `username` VARCHAR(30) NOT NULL UNIQUE, `email` VARCHAR(256) NOT NULL UNIQUE, `hashedPassword` VARCHAR BINARY(255) NOT NULL, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `Bookings` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `spotId` INTEGER NOT NULL, `userId` INTEGER NOT NULL, `startDate` DATETIME NOT NULL, `endDate` DATETIME NOT NULL, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `Spots` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `ownerId` INTEGER NOT NULL, `address` VARCHAR(255) NOT NULL, `city` VARCHAR(255) NOT NULL, `state` VARCHAR(255) NOT NULL, `country` VARCHAR(255) NOT NULL, `lat` DECIMAL NOT NULL, `lng` DECIMAL NOT NULL, `name` VARCHAR(255) NOT NULL, `description` VARCHAR(255) NOT NULL, `price` DECIMAL NOT NULL, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `SpotImages` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `spotId` INTEGER NOT NULL, `url` VARCHAR(255) NOT NULL, `preview` TINYINT(1) NOT NULL DEFAULT 1, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `ReviewImages` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `reviewId` INTEGER, `url` VARCHAR(255), `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `Reviews` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `spotId` INTEGER NOT NULL, `userId` INTEGER NOT NULL, `review` VARCHAR(255) NOT NULL, `stars` INTEGER NOT NULL, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
